import { Fragment } from "react";
import { useParams,Route } from "react-router-dom";
import Comments from '../components/comments/Comments';

const DUMMYDATA=[
    {id:'q1', author:'chula', text:'learning react is cool and nice'},
    {id:'q2', author:'Emmanuel', text:'learning hooks is cool'}

];
const AllQuote =()=>{
    const params= useParams();

    const quotes=DUMMYDATA.find(quote=>quote.id===params.quoteId);
    return(
     <Fragment>
            Detail Quotes
            <p>{params.quoteId}</p>
            <Route path={`/quotes/${params.quoteId}/comments`}>
               <Comments />
            </Route>
     </Fragment>
    )
 }
 export default AllQuote;
