import QuoteForm from '../components/quotes/QuoteForm';
import { useHistory } from 'react-router-dom';
import useHttp from '../hooks/use-http';
import { addQuote } from '../lib/api';


const NewQuote =()=>{
    //extracting the data needed using restructuring
   const {sendRequest, status}= useHttp(addQuote);
    //useHistory allow changing of the browser history
    const history = useHistory();
     const onAddQuoteHandler=(quoteData)=>{
         console.log(quoteData);
         sendRequest(addQuote);
         //programic navigation
     history.push('/quotes');
    };
    return <QuoteForm onAddQuote={onAddQuoteHandler}/>
    
 }
 export default NewQuote;
