import { useEffect } from "react";
import QuoteList from "../components/quotes/QuoteList";
import LoadingSpinner from '../components/UI/LoadingSpinner';
import NoQuotesFound from '../components/quotes/NoQuotesFound';
import useHttp from '../hooks/use-http';
import { getAllQuote } from '../lib/api';


const DUMMYDATA=[
    {id:'q1', author:'chula', text:'learning react is cool and nice'},
    {id:'q2', author:'Emmanuel', text:'learning hooks is cool'}

];
const AllQuote =()=>{

   const {
    sendRequest, status, data:loadedQuotes, error
     }= useHttp(getAllQuote, true)

     useEffect(()=>{
        sendRequest();
     },[sendRequest]);
     
     if(status==='pending'){
        <div className="centered">
           <LoadingSpinner />
        </div>
     }
     if(error){
        return <p className="centered focused">{error}</p>
     }
   
   return(
    <div>
        <QuoteList quotes={DUMMYDATA}/>
    </div>
   )
}
export default AllQuote;
